import React, { useState, useRef } from "react";
import "./App.css";
import background from "./background.jpg"; // import your image

function App() {
  const [activePage, setActivePage] = useState("home");
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const fileInputRef = useRef(null);

  // handle file upload
  const handleFileChange = (event) => {
    const files = Array.from(event.target.files);
    if (files.length + uploadedFiles.length <= 2) {
      setUploadedFiles([...uploadedFiles, ...files]);
    } else {
      alert("You must upload only 2 files.");
    }
  };

  const handleUploadClick = () => {
    fileInputRef.current.click();
  };

  // run comparison dummy function
  const handleRunComparison = () => {
    console.log("Running comparison on:", uploadedFiles);
    alert("Comparison complete! (placeholder)");
  };

  // export dummy results
  const handleExport = () => {
    const data = "cell_id,x,y\n1,12,34\n2,56,78"; // dummy CSV
    const blob = new Blob([data], { type: "text/csv" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "cell_points.csv";
    link.click();
  };

  // reset files
  const handleReset = () => {
    setUploadedFiles([]);
    fileInputRef.current.value = null;
  };

  return (
    <div
      className="app"
      style={{
        backgroundImage: `url(${background})`,
      }}
    >
      {/* Header */}
      <div className="header">
        <div className="left">
          <button
            className={activePage === "home" ? "active" : ""}
            onClick={() => setActivePage("home")}
          >
            Home
          </button>
          <button
            className={activePage === "about" ? "active" : ""}
            onClick={() => setActivePage("about")}
          >
            About
          </button>
        </div>
        <div className="right">
          <button className="upload-btn" onClick={handleUploadClick}>
            Upload (2 required)
          </button>
          <input
            type="file"
            ref={fileInputRef}
            style={{ display: "none" }}
            onChange={handleFileChange}
            multiple
          />
        </div>
      </div>

      {/* Content */}
      <div className={`content ${activePage}`}>
        {activePage === "home" && (
          <>
            <h1>WELCOME</h1>
            <p>
              Mineral exploration in Western Australia is a critical activity
              that supports the greater mining industry by discovering and
              defining critical resource deposits. The earliest phase of
              exploration seeks to identify regions which likely contain
              precious minerals at economic scale...
            </p>
          </>
        )}

        {activePage === "about" && (
          <>
            <h2>About The Project</h2>
            <p>
              This project focuses on Tellurium (Te) assay values within a subset
              of this publicly hosted data. Tellurium is a critical mineral...
            </p>
          </>
        )}

        {/* File Info + Action Buttons */}
        <div style={{ marginTop: "20px" }}>
          <h3>Uploaded Files:</h3>
          <ul>
            {uploadedFiles.map((file, idx) => (
              <li key={idx}>{file.name}</li>
            ))}
          </ul>

          <div style={{ marginTop: "10px" }}>
            <button
              disabled={uploadedFiles.length !== 2}
              onClick={handleRunComparison}
            >
              Run Comparison
            </button>

            <button
              disabled={uploadedFiles.length !== 2}
              onClick={handleExport}
            >
              Export Cell Points
            </button>

            <button onClick={handleReset}>Reset</button>
          </div>
        </div>
      </div>

      {/* Footer */}
      <div className="footer">
        Â© Group15 Capstone Project | All rights reserved
      </div>
    </div>
  );
}

export default App;
